import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Router } from '@angular/router';

@Injectable({
  providedIn: 'root'
})
export class ProductServiceService {

  constructor(private http:HttpClient,private route:Router) {this.addProduct }

  getProducts(){
    return this.http.get('http://localhost:3000/products');
  }

  getCategories(){
    return this.http.get('http://localhost:3000/categories');
  }

  getTopTrending(){
    return this.http.get('http://localhost:3000/products?_limit=4');
  }

  getTopSelling(){
    return this.http.get('http://localhost:3000/products?_limit=10');
  }

  getCarousel(){
    return this.http.get('http://localhost:3000/carousel');
  }

  getCartItems(){
    return this.http.get(`http://localhost:3000/cart`);
  }

  product:any="";

  addToCart(id:any){
    console.log("Product index is ",id);
    
    this.http.get(`http://localhost:3000/products?id=${id}`,{observe:'response'}).subscribe((result:any)=>{
      // console.log("Cart Product",result);
      
      // console.log("Result Body is",result.body);
      console.log("Final Product to be Addded",this.product);
      
      if(result && result.body && result.body.length){
        this.product= result.body;
        this.addProduct();
        for(let i:number=0;i<this.product.length;i++){
          this.product[i].quantity=1;
          
        }
        // this.route.navigate(['cartListing']);
      }
    })
    
  }

  addProduct(){
    console.log("Product in Cart",this.product);
    
    this.http.post('http://localhost:3000/cart',this.product,{observe:'response'}).subscribe((result:any)=>{
      console.log("Products of Cart",result);
      this.route.navigate(['cartListing']);
    })
  }

}
