import { Component } from '@angular/core';

@Component({
  selector: 'app-order-details',
  templateUrl: './order-details.component.html',
  styleUrls: ['./order-details.component.css']
})
export class OrderDetailsComponent {

  data=JSON.stringify(localStorage.getItem('customer'));
  customerDetails=JSON.parse(this.data);

  ngOnInit() {
    console.log(this.customerDetails);
  }

}
<div class="companyDetails">
    <h1>Sanjay Bhokare Group of Institutes, Miraj</h1>
    <pre class="companyInfo">Shri Ambabai Talim Sanstha's Sanjay Bhokare Group Of Institutes Miraj Tilak Nagar, Miraj-Sangli Road, Post-Wanlesswadi, Miraj, Dist: Sangli - Pin-416414.</pre>
    <pre class="companyInfo">Email -  director@sbgimiraj.org</pre>
    <pre class="companyInfo">Mobile -  +91-9923259000</pre>
</div>
<div class="customerDetails">
    <div class="billingAdd">
        {{customerDetails}}
    </div>
    <div class="shippingAdd">
        {{customerDetails}}
    </div>
</div>
