import { Component } from '@angular/core';



@Component({
  selector: 'app-carousel',
  templateUrl: './carousel.component.html',
  styleUrls: ['./carousel.component.css'],
})
export class CarouselComponent {

  currentIndex: number = 0;

  carouselArr:any[]=[
    {image:'.assets/product1.jpeg', text:'Sample Text1',sub:"Lorem, ipsum dolor sit amet consectetur adipisicing elit. Veritatis consectetur tempora perspiciatis sint aspernatur nemo amet beatae."},
    {image:'.assets/product2.jpeg', text:'Sample Text2',sub:""},
    {image:'.assets/product3.jpeg', text:'Sample Text3',sub:""},
    {image:'.assets/product4.jpeg', text:'Sample Text4',sub:""},
    {image:'.assets/product5.jpeg', text:'Sample Text5',sub:""},
  ];


  next() {
    this.currentIndex = (this.currentIndex + 1) % this.carouselArr.length;
  }

  prev() {
    this.currentIndex = (this.currentIndex - 1 + this.carouselArr.length) % this.carouselArr.length;
  }

}
<div class="carousel">
    <div class="carouselContainer">
        <div class="text">
            <p>{{carouselArr[currentIndex].text}}</p>
        </div>
        <div class="image">
            <img [src]="carouselArr[currentIndex].image" [alt]="carouselArr[currentIndex]">
        </div>
    </div>
</div>
