import { Component } from '@angular/core';
import { carousel } from '../dataTypes';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

  currentIndex: number = 0;

  carouselArr:carousel[]=[
    {image:'./assets/product1.jpeg', text:'Sample Text1'},
    {image:'./assets/product2.jpeg', text:'Sample Text2'},
    {image:'./assets/product3.jpeg', text:'Sample Text3'},
    {image:'./assets/product4.jpeg', text:'Sample Text4'},
    {image:'./assets/product5.jpeg', text:'Sample Text5'},
  ];


  next() {
    this.currentIndex<this.carouselArr.length-1?this.currentIndex++:this.currentIndex=0;
    console.log(this.currentIndex);

  }

  prev() {
    this.currentIndex>0?this.currentIndex--:this.currentIndex=this.carouselArr.length-1;
    console.log(this.currentIndex);
  }


  // Categories Login Starts here



  categories:any[]=[
    {name:"grocery",image:'./assets/logo.png',productCount:2,altText:"Categoory Thumbnail Image"},
    {name:"grocery",image:'./assets/logo.png',productCount:2,altText:"Categoory Thumbnail Image"},
    {name:"grocery",image:'./assets/logo.png',productCount:2,altText:"Categoory Thumbnail Image"},
    {name:"grocery",image:'./assets/logo.png',productCount:2,altText:"Categoory Thumbnail Image"},
    {name:"grocery",image:'./assets/logo.png',productCount:2,altText:"Categoory Thumbnail Image"},
    {name:"grocery",image:'./assets/logo.png',productCount:2,altText:"Categoory Thumbnail Image"},
    {name:"grocery",image:'./assets/logo.png',productCount:2,altText:"Categoory Thumbnail Image"},
    {name:"grocery",image:'./assets/logo.png',productCount:2,altText:"Categoory Thumbnail Image"},
    {name:"grocery",image:'./assets/logo.png',productCount:2,altText:"Categoory Thumbnail Image"},
    {name:"grocery",image:'./assets/logo.png',productCount:2,altText:"Categoory Thumbnail Image"},
    {name:"grocery",image:'./assets/logo.png',productCount:2,altText:"Categoory Thumbnail Image"},

  ]

}
<app-navbar></app-navbar>


<!-- Carousel Element -->
<div class="carousel">
    <div class="carouselContainer">
        <a (click)="prev()" id="left"><i class="fa-solid fa-chevron-left"></i></a>
        <div class="text">
            <p>{{carouselArr[currentIndex].text}}</p>
        </div>
        <div class="image">
            <img [src]="carouselArr[currentIndex].image" [alt]="carouselArr[currentIndex]">
            <!-- <img src="./assets/product1.jpeg" alt="Carousel Image"> -->
        </div>
        <a (click)="next()" id="right"><i class="fa-solid fa-chevron-right"></i></a>
    </div>
</div>



<!-- Categories Elements -->

<div class="categories">
    <div class="head">
        <h2>Categories</h2>
        <a href="#"><small>ViewAll</small></a>
    </div>
    <ul class="categotyList">
        <div class="categoryItem">
            <li *ngFor="let category of categories"><img [src]="category.image" alt="category.altText"> <p id="categoryName">{{category.name | titlecase}}</p> <p id="productCount" >Product Count is: {{category.productCount}}</p></li>
        </div>
    </ul>
</div>
