import { Component } from '@angular/core';
import { NgbCarouselConfig } from '@ng-bootstrap/ng-bootstrap';


@Component({
  selector: 'app-carousel',
  templateUrl: './carousel.component.html',
  styleUrls: ['./carousel.component.css'],
  providers: [NgbCarouselConfig]
})
export class CarouselComponent {

  carouselArr:any[]=[
    {image:'.assets/product1.jpeg', text:'Sample Text1'},
    {image:'.assets/product2.jpeg', text:'Sample Text2'},
    {image:'.assets/product3.jpeg', text:'Sample Text3'},
    {image:'.assets/product4.jpeg', text:'Sample Text4'},
    {image:'.assets/product5.jpeg', text:'Sample Text5'},
  ];


  constructor(config: NgbCarouselConfig) {
    // customize default values of carousels used by this component tree
    config.interval = 2000; // slide interval in milliseconds
    config.wrap = true; // Whether the carousel should cycle continuously or have hard stops
    config.keyboard = false; // Whether keyboard navigation should be enabled
    config.pauseOnHover = true; // Whether carousel should pause on mouseover
  }

}
