import { Component } from '@angular/core';
import { carousel } from 'src/app/dataTypes';



@Component({
  selector: 'app-carousel',
  templateUrl: './carousel.component.html',
  styleUrls: ['./carousel.component.css'],
})
export class CarouselComponent {

  currentIndex: number = 0;

  carouselArr:carousel[]=[
    {image:'.assets/product1.jpeg', text:'Sample Text1'},
    {image:'.assets/product2.jpeg', text:'Sample Text2'},
    {image:'.assets/product3.jpeg', text:'Sample Text3'},
    {image:'.assets/product4.jpeg', text:'Sample Text4'},
    {image:'.assets/product5.jpeg', text:'Sample Text5'},
  ];


  next() {
    this.currentIndex = (this.currentIndex + 1) % this.carouselArr.length;
  }

  prev() {
    this.currentIndex = (this.currentIndex - 1 + this.carouselArr.length) % this.carouselArr.length;
  }

}
<div class="carousel">
    <div class="carouselContainer">
        <i class="fa-solid fa-chevron-left"></i>
        <div class="text">
            <p>{{carouselArr[currentIndex].text}}</p>
        </div>
        <div class="image">
            <!-- <img [src]="carouselArr[currentIndex].image" [alt]="carouselArr[currentIndex]"> -->
            <img [src]="carouselArr[0].image" alt="Carousel Image">
        </div>
        <i class="fa-solid fa-chevron-right"></i>
    </div>
</div>
