    <pre class="companyInfo">Email -  director@sbgimiraj.org</pre>
    <pre class="companyInfo">Mobile -  +91-9923259000</pre>
</div>
<div class="customerDetails">
    <div class="billingAdd">
        {{customerDetails}}
    </div>
    <div class="shippingAdd">
        {{customerDetails}}
    </div>
</div>
