import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { cartItem, topSelling } from 'src/app/dataTypes';
import { ProductServiceService } from 'src/app/services/product-service.service';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css'],
})
export class CartComponent {
  [x: string]: any;

  constructor(private route: Router, private product:ProductServiceService) {
    this.getCartItems();
    this.total();
  }

  ngOnInit() {
    this.total();
  }

  favouriteItem: boolean = false;
  subTotal: number = 0;

  addFavourite() {
    alert('product added to Favourites');
    this.favouriteItem = true;
  }

  deleteItems() {
    alert('product deleted from Cart');
    this.route.navigate(['produtList']);
  }

  add(index: number, val: number, price: number) {
    this.cartItems[index].quantity++;
    this.total();
  }

  remove(index: number, val: number, price: number) {
    if (val > 1) {
      this.cartItems[index].quantity--;
      this.total();
    }
  }

  viewProduct() {
    this.route.navigate(['produtList']);
  }

  total() {
    let subTotal = 0;
    this.subTotal = 0;
    for (let i = 0; i < this.cartItems.length; i++) {
      const element = this.cartItems[i];
      if (element.salePrice) { 
        let productTotal = element.quantity * element.salePrice;
        subTotal = subTotal + productTotal;
        console.log('This is SubTotal', this.subTotal);
        console.log('This is sellPrice', element.salePrice);
      } else {
        let productTotal = element.quantity * element.mrp;
        subTotal = subTotal + productTotal;
      }
    }

    this.subTotal = subTotal;
  }

  cartItems:any=[]
  getCartItems(){
    this.product.getCartItems().subscribe((result:any)=>{
      console.log("Cart Items",result);
      result.quantity=1;
      this.cartItems=result;
      console.log("Final Response",this.cartItems);
      this.total();
      
    })
  }
}
<div class="cartPageHeader">
  <h2>Your Cart Items!</h2>
</div>

<div class="cartPageItems">
  <table>
    <tr>
      <th>Sr.No</th>
      <th>Product Name</th>
      <th>Quantity</th>
      <th>Amount</th>
      <th>Action</th>
    </tr>
    <tr *ngFor="let item of cartItems; let i = index">
      <td>{{i+1}}</td>
      <td>{{ item.name }}</td>
      <td id="quantity" *ngIf="item.salePrice">
        <button (click)="remove(i, item.quantity, item.salePrice)">-</button>
        {{ item.quantity }}
        <button (click)="add(i, item.quantity, item.salePrice)">+</button>
      </td>
      <td id="quantity" *ngIf="!item.salePrice">
        <button (click)="remove(i, item.quantity, item.mrp)">-</button>
        {{ item.quantity }}
        <button (click)="add(i, item.quantity, item.mrp)">+</button>
      </td>
      <td *ngIf="item.salePrice">{{ item.quantity * item.salePrice }}</td>
      <td *ngIf="!item.salePrice">{{ item.quantity * item.mrp }}</td>
      <td class="actions">
        <i class="fa-regular fa-trash-can" (click)="deleteItems()"></i>
        <i class="fa-regular fa-eye" (click)="viewProduct()"></i>
      </td>
    </tr>
    <tr *ngFor="let item of cartItems"></tr>
  </table>
  <div class="cartFooter">
    {{ subTotal }}
  </div>
</div>
