import { Component, EventEmitter, Output } from '@angular/core';
import { Router } from '@angular/router';
import { cartItem, topSelling } from 'src/app/dataTypes';
import { ProductServiceService } from 'src/app/services/product-service.service';
import { UserService } from 'src/app/services/user.service';


@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css'],
})
export class CartComponent {
  [x: string]: any;

  cartItems:any=[];
  subTotal: number = 0;


  constructor(private route: Router, private product:ProductServiceService,private user:UserService) {
    
    this.total();
  }

  ngOnInit() {
    this.total();
    this.getCartItems();
  }


  add(index: number, val: number) {
    this.cartItems[index].quantity++;
    this.total();
  }

  remove(index: number, val: number) {
    if (val > 1) {
      this.cartItems[index].quantity--;
      this.total();
    }
  }

  viewProduct() {
    this.route.navigate(['produtList']);
    alert('View Product Called')
  }


  deleteItems(id:any,index:any) {
    // alert('product deleted from Cart');
    this.product.removeProduct(id);
        this.cartItems.splice(index,1);
        this.total();
  }

  total() {
    let subTotal = 0;
    this.subTotal = 0;
    for (let i = 0; i < this.cartItems.length; i++) {
      const element = this.cartItems[i];
      if (element.salePrice) { 
        let productTotal = element.quantity * element.salePrice;
        subTotal = subTotal + productTotal;
      } else {
        let productTotal = element.quantity * element.mrp;
        subTotal = subTotal + productTotal;
      }
    }

    this.subTotal = subTotal;
  }

  getCartItems(){
    this.product.getCartItems().subscribe((result:any)=>{
      result.quantity=1;
      this.cartItems=result;
      this.total();
    })
  }


  checkout(){

        if(this.cartItems.length>0){
          if(localStorage.getItem('customer')){
            this.route.navigate(['checkout']);
            this.product.addProductInOrder(this.cartItems);
          }else{
            alert('Please Login / Register before checkout');
            this.route.navigate(['login']);
          }
        }else{
          alert('You have to Add Atleast One Item in Cart');
          this.route.navigate(['productList']);
        }

  
  }


  addProduct(){
    this.route.navigate(['productList'])
  }

}
<div class="cartPageHeader">
  <h2>Your Cart Items!</h2>
</div>

<div class="cartPageItems">
  <table>
    <tr>
      <th>Sr.No</th>
      <th>Product Name</th>
      <th>Quantity</th>
      <th>Amount</th>
      <th>Action</th>
    </tr>
    <tr *ngFor="let item of cartItems; let i = index">
      <td>{{i+1}}</td>
      <td>{{ item.name }}</td>
      <td id="quantity" *ngIf="item.salePrice">
        <button (click)="remove(i, item.quantity)">-</button>
        {{ item.quantity }}
        <button (click)="add(i, item.quantity)">+</button>
      </td>
      <td id="quantity" *ngIf="!item.salePrice">
        <button (click)="remove(i, item.quantity)">-</button>
        {{ item.quantity }}
        <button (click)="add(i, item.quantity)">+</button>
      </td>
      <td *ngIf="item.salePrice">{{ item.quantity * item.salePrice }}</td>
      <td *ngIf="!item.salePrice">{{ item.quantity * item.mrp }}</td>
      <td class="actions">
        <i class="fa-regular fa-eye" (click)="viewProduct()"  id="view"></i>
        <i class="fa-regular fa-trash-can" (click)="deleteItems(item.id,i)"  id="delete"></i>
      </td>
    </tr>
    <tr *ngFor="let item of cartItems"></tr>
  </table>
  <div class="cartFooter">
    <button (click)="addProduct()" id="addProduct">Add Product</button>
    <p>{{ subTotal | currency:'INR':'symbol-narrow':'.2-2' }}</p>
    <button (click)="checkout()" id="checkout">Checkout</button>
  </div>
</div>
