import { Component } from '@angular/core';



@Component({
  selector: 'app-carousel',
  templateUrl: './carousel.component.html',
  styleUrls: ['./carousel.component.css'],
})
export class CarouselComponent {

  currentIndex: number = 0;

  carouselArr:any[]=[
  '.assets/product1.jpeg',
  '.assets/product2.jpeg',
  '.assets/product3.jpeg',
  '.assets/product4.jpeg',
  '.assets/product5.jpeg',
  ];


  next() {
    this.currentIndex = (this.currentIndex + 1) % this.carouselArr.length;
  }

  prev() {
    this.currentIndex = (this.currentIndex - 1 + this.carouselArr.length) % this.carouselArr.length;
  }

}
<div class="carousel">
    <div class="carouselContainer">
        <div class="image">
            <img [src]="carouselArr[currentIndex]" [alt]="carouselArr[currentIndex]">
        </div>
    </div>
</div>
